INSERT INTO grr_page VALUES('mails_retardrestitution2_de-de', 'GRR: Meldung %ressource% - %resadatedebut% - Verzögerungsbenachrichtigung', '<p>%nometablissement% -&nbsp;Automatische Nachricht von der GRR Webseite&nbsp; : %urlgrr%</p><br><p>Sofern ich mich nicht irre, wurde die von Ihnen geliehene ressource&nbsp;<b>%ressource%</b> (%domaine%) nicht zurückgegeben. Wenn es sich hierbei um einen Fehler handelt, ignorieren Sie diese E-Mail bitte.</p><p><br></p><p>Reservierung im Namen von : %logincompletbeneficiaire%</p><p>Beginn der Reservierung : %resadatedebut%<br></p><p>Dauer : %resaduree%</p><p>%resanom%<br></p><p>%resadescription%<br></p><p>%resachampsadditionnels%</p><p>Art : %resatype%</p><p>%resaperiodique%</p>%resaconfirmation%<p><br></p><p></p>------<p></p><p>%raisonmail%</p><p>Wenn\r\n sie diese automatische Nachricht nicht mehr erhalten wollen, wenden Sie\r\n sich bitte an Ihren GRR-Systemverwalter : %webmasteremail%</p><p></p>', 1);
INSERT INTO grr_page VALUES('mails_retardrestitution2_en-gb', 'GRR : notice %ressource% - %resadatedebut% - Notifying a delay', '<p>%nometablissement% -&nbsp;Automatic message emitted by GRR site : %urlgrr%</p><br><p>Errors excepted, the resource&nbsp;<b>%ressource% </b>(%domaine%) you borrowed was not restored. If it is an error, please not take this mail into account.</p><p><br></p><p>Reservation in the name of : %logincompletbeneficiaire%</p><p>Reservation will start : %resadatedebut%<br></p><p>Duration : %resaduree%</p><p>%resanom%<br></p><p>%resadescription%<br></p><p>%resachampsadditionnels%</p><p>Type : %resatype%</p><p>%resaperiodique%</p>%resaconfirmation%<p><br></p><p></p>------<p></p><p>%raisonmail%<br>If you no longer wish to get these automatic mails, write to GRR manager : %webmasteremail%</p><p></p>', 1);
INSERT INTO grr_page VALUES('mails_retardrestitution2_es-es', 'GRR : opinión %ressource% - %resadatedebut% - Notificación de retraso', '<p>%nometablissement% -&nbsp;Mensaje automático emitido por el GRR : %urlgrr%</p><br><p>A menos que me equivoque, el recurso <b>%ressource%</b> (%domaine%)que tomó prestado no se ha devuelto. Si se trata de un error, ignore este correo.</p><p><br></p><p>Reserva por cuenta del usuario : %logincompletbeneficiaire%</p><p>Principio de la reserva : %resadatedebut%<br></p><p>Duración : %resaduree%</p><p>%resanom%<br></p><p>%resadescription%<br></p><p>%resachampsadditionnels%</p><p>Tipo : %resatype%</p><p>%resaperiodique%</p>%resaconfirmation%<p><br></p><p></p>------<p></p><p>%raisonmail%</p><p>Si usted no desea recibir estos mensajes automáticos, escriba al gestor de GRR : %webmasteremail%</p><p></p>', 1);
INSERT INTO grr_page VALUES('mails_retardrestitution2_fr-fr', 'GRR : avis %ressource% - %resadatedebut% - Notification de retard', '<p>%nometablissement% - Message automatique émis par le site GRR : %urlgrr%</p><br><p>Sauf erreur, la ressource <b>%ressource%</b> (%domaine%) que vous avez empruntée n\'a pas été restituée. S\'il s\'agit d\'une erreur, veuillez ne pas tenir compte de ce courrier.</p><p><br></p><p>Réservation au nom de l\'utilisateur : %logincompletbeneficiaire%</p><p>Début de la réservation : %resadatedebut%<br></p><p>Durée : %resaduree%</p><p>%resanom%<br></p><p>%resadescription%<br></p><p>%resachampsadditionnels%</p><p>Type : %resatype%</p><p>%resaperiodique%</p>%resaconfirmation%<p><br></p><p></p>------<p></p><p>%raisonmail%<br>Si vous ne souhaitez plus recevoir ces messages automatiques, écrivez en ce sens au gestionnaire de GRR : %webmasteremail%</p><p></p>', 1);
INSERT INTO grr_page VALUES('mails_retardrestitution2_it-it', 'GRR : opinione %ressource% - %resadatedebut% - Notifica di ritardo', '<p>%nometablissement% -&nbsp;Messaggio automatico emesso dal sito GRR : %urlgrr%</p><br><p>Se non sbaglio, la risorsa <b>%ressource%</b> (%domaine%) che hai preso in prestito non è stata restituita. Se si tratta di un errore, si prega di ignorare questa mail.</p><p><br></p><p>Prenotazione per conto dell\'utente : %logincompletbeneficiaire%</p>Inizio della prenotazione : %resadatedebut%<p></p><p>Durata : %resaduree%</p><p></p><p>%resanom%<br></p><p>%resadescription%<br></p><p>%resachampsadditionnels%</p><p>Tipo : %resatype%</p><p>%resaperiodique%</p>%resaconfirmation%<p><br></p><p></p>------<p></p><p>%raisonmail%</p><p>Se non desiderate ricevere più questi messaggi automatici, scrivete in questo senso all\'amministratore di GRR : %webmasteremail%</p><p></p><p></p>', 1);
INSERT INTO grr_page VALUES('mails_retardrestitution_de-de', 'GRR: Meldung %ressource% - %resadatedebut% - Verzögerungsbenachrichtigung', '<p>%nometablissement% -&nbsp;Automatische Nachricht von der GRR Webseite :  %urlgrr%</p><p>Ressource<b> %ressource%</b> (%domaine%) <u>wurde nicht zurückgegeben</u>.<br></p>Name des Kreditnehmers : %logincompletbeneficiaire%<p></p><p></p><p>%maildestinataire%<br></p>Wenn die Ressource zurückgegeben wurde, gehen Sie zur folgenden Adresse, um den Status der Reservierung zu ändern:<p></p>%urldetail%<p></p><p></p><p><br></p>------<p></p><p>%raisonmail%<br>Wenn\r\n sie diese automatische Nachricht nicht mehr erhalten wollen, wenden Sie\r\n sich bitte an Ihren GRR-Systemverwalter : %webmasteremail%</p><p></p>', 1);
