INSERT INTO grr_page VALUES('mails_demandecompte2_de-de', '[Ergebnis] Anfrage zur Kontoerstellung', '<p>Guten Morgen<br>Ihre Anfrage zur Erstellung eines Kontos auf %urlgrr% wurde angenommen.</p><p><br>Sie können sich jetzt unten mit Ihrem Benutzernamen anmelden.<br>Bezeichner: %identifiant%<br>Passwort: Das Passwort, das Sie bei Ihrer Anfrage angegeben haben.</p><p><br></p><p>---</p><p><span class=\"HwtZe\" lang=\"de\"><span class=\"jCAhz ChMk0b\"><span class=\"ryNqvb\">Bitte antworten Sie nicht auf diese Email.</span></span></span></p><p></p>', 1);
INSERT INTO grr_page VALUES('mails_demandecompte2_en-gb', '[Result] Account creation request %formnom% %formprenom%', '<p><span class=\"HwtZe\" lang=\"en\"><span class=\"jCAhz ChMk0b\"><span class=\"ryNqvb\">Good morning,<br></span></span><span class=\"jCAhz ChMk0b\"><span class=\"ryNqvb\">Your request to create an account on %urlgrr% has been accepted.<br></span></span><span class=\"jCAhz ChMk0b\"><span class=\"ryNqvb\">You can now log in with your username below.<br></span></span><span class=\"jCAhz ChMk0b\"><span class=\"ryNqvb\">Identifier: %identifiant%<br></span></span><span class=\"jCAhz ChMk0b\"><span class=\"ryNqvb\">Password: The one you provided during your request.</span></span></span></p><p><span class=\"HwtZe\" lang=\"en\"><span class=\"jCAhz ChMk0b\"><span class=\"ryNqvb\"><br></span></span></span></p><p><span class=\"HwtZe\" lang=\"en\"><span class=\"jCAhz ChMk0b\"><span class=\"ryNqvb\">---</span></span></span></p><p><span class=\"HwtZe\" lang=\"en\"><span class=\"jCAhz ChMk0b\"><span class=\"ryNqvb\">Please do not reply to this email.</span></span></span></p><p></p>', 1);
INSERT INTO grr_page VALUES('mails_demandecompte2_es-es', '[Resultado] Solicitud de creación de cuenta', '<p>Buenos dias<br>Su solicitud para crear una cuenta en %urlgrr% ha sido aceptada.<br>Ahora puede iniciar sesión con su nombre de usuario a continuación.<br>Identificador: %identifiant%<br>Contraseña: La que usted proporcionó durante su solicitud.</p><p><br></p><p>---</p><p><span class=\"HwtZe\" lang=\"es\"><span class=\"jCAhz\"><span class=\"ryNqvb\">\r\n</span></span><span class=\"jCAhz ChMk0b\"><span class=\"ryNqvb\">Por favor no responder a este email.</span></span></span></p><p></p>', 1);
INSERT INTO grr_page VALUES('mails_demandecompte2_fr-fr', '[Résultat] Demande de création de compte %formnom% %formprenom%', '<p>Bonjour,</p><p>Votre demande création de compte sur&nbsp;%urlgrr% a été acceptée.<br>Vous pouvez désormais vous connecter avec votre identifiant ci-dessous.<br>Identifiant : %identifiant%<br>Mot de passe : Celui que vous avez renseigné lors de votre demande.</p><p><br></p><p>---</p>Veuillez ne pas répondre à ce mail.<p></p>', 1);
INSERT INTO grr_page VALUES('mails_demandecompte2_it-it', '[Risultato]  Richiesta di creazione dell\'account', '<p>Buongiorno<br>La tua richiesta di creare un account su %urlgrr% è stata accettata.<br>Ora puoi accedere con il tuo nome utente qui sotto.<br>Identificatore: %identifiant%<br>Password: quella che hai fornito durante la richiesta.</p><p><br></p><p>---</p><p><span class=\"HwtZe\" lang=\"it\"><span class=\"jCAhz ChMk0b\"><span class=\"ryNqvb\">Per favore non rispondere a questa email.</span></span></span></p><p></p>', 1);
